import React from 'react'

class Search extends React.Component {

    state =  {
        Search : '',

    }

    handleKey = (event) => {
        if(event.key == 'Enter') {
            this.props.searchValue(this.state.Search)
        }
    }

    render(){
        return( 
            <div className="row">
            <div className="input-field col s12">
              <input onChange={(e)=> this.setState({Search : e.target.value}) } value={this.state.Search} id="text" type="search" placeholder='which movie you wanna search?' className="validate" />
  <button onKeyDown={this.handleKey} className="btn waves-effect waves-light blue darken-2" type="submit" name="action">Submit
  </button>
            </div>
          </div>
        )
    }


}

export default Search
