import React from 'react'
import Movies from '../components/Movies'
import Preloader from '../components/Preloader'
import Search from '../components/Search'


class Main extends React.Component {
  state = {
    movies : [],
  }

  componentDidMount() {
    fetch('http://www.omdbapi.com/?apikey=95405593&s=matrix').then(response => response.json())
    .then(data => this.setState({movies: data.Search}))
  }

  searchValue = (str) =>  {
    fetch(`http://www.omdbapi.com/?apikey=95405593&s=${str}`).then(response => response.json())
    .then(data => this.setState({movies: data.Search}))

  }
  searchMovies = (str) => {
    fetch(`http://www.omdbapi.com/?apikey=78584b3c&s=${str}`)
        .then((response) => response.json())
        .then((data) => this.setState({ movies: data.Search }));
};
  render () {
    const {movies} = this.state
    return (
      <main className='container content'>
        <Search  searchValue={this.searchValue} />
          { movies.length ? (<Movies movies={this.state.movies} />) : <Preloader/> }
      </main>
    )
  }

}

export default Main
