import React from 'react'

class Search extends React.Component {

    state =  {
        Search : '',

    }

    handleKey = (event) => {
        if(event.key === 'Enter') {
            this.props.searchValue(this.state.Search)
        }
    }

    handleFilter = (event) => {
        this.setState({type : event.target.dataset.type})
    }

    render(){
        return( 
            <div className="row">
            <div className="input-field col s12">
              <input onChange={(e)=> this.setState({Search : e.target.value}) } value={this.state.Search}  onKeyDown={this.handleKey} id="text" type="search" placeholder='which movie you wanna search?' className="validate" />
  <button onClick={() =>  this.props.searchValue(this.state.Search)} className="btn waves-effect waves-light blue darken-2" type="submit" name="action">Submit
  </button>
            </div>
            <div className='filter'>
<p>
      <label>
        <input name="type" type="radio" datatype='all' checked  onChange={this.handleFilter}/>
        <span>all</span>
      </label>
    </p>
    <p>
      <label>
        <input name="type" type="radio" datatype='movie' checked  onChange={this.handleFilter}/>
        <span>Movies only</span>
      </label>
    </p>
    <p>
      <label>
        <input name="type" type="radio" datatype='series' checked onChange={this.handleFilter} />
        <span>Series only</span>
      </label>
    </p>
</div>
          </div>
        )
    }


}

export default Search
